<?phpclass authentication extends CI_Controller{    private $sign_out;    private $pageData;    function __construct() {        parent::__construct();        $this->load->helper('url');        include_once(APPPATH.'controllers/common_site_setting.php');        include_once(APPPATH.'controllers/class_file/top_container_inf.php');         if($this->session->userdata('admin_inf')){           redirect(base_url().'admin/dashboard');         }        $this->pageData['sign_out']=&$this->sign_out;        $this->sign_out=FALSE;            }    function index(){        include_once(APPPATH . 'controllers/class_file/Profile/C_Aggrigates.php');        $this->load->model('m_user_trait_final_values');        $c_aggrigates = new C_Aggrigates();        $this->pageData['c_aggrigates'] = $c_aggrigates;        $this->pageData['top2_ranked_user'] = $this->m_user_trait_final_values->get_top2_rank_userinfo();        $this->pageData['top3_popular_user'] = $this->m_user_trait_final_values->get_top3_populer_userinfo();        $this->load->view('admin_login_view',$this->pageData);     }    function authenticate(){       $this->load->model('m_admin_login');       $this->email=trim($this->input->post('email'));       $this->password=trim($this->input->post('password'));             if($this->m_admin_login->authenticate($this->email,$this->password))        {            if($this->m_admin_login->is_active_user($this->email))            {                $u_id=$this->m_admin_login->get_admin_id($this->email);                                $sessionData=array(		        'u_id'=>$u_id,			'email'=>$this->email		);		$this->session->set_userdata('admin_inf',$sessionData);                redirect(base_url().'admin/dashboard');            }            else            {                  $this->session->set_flashdata('loginError','Invalid Email Or Password');                redirect(base_url().'authentication');            }                    }         else            {                 $this->session->set_flashdata('loginError','Invalid Email Or Password');                redirect(base_url().'authentication');            }                        }}?>