<?php	 class c_graph_frequency_month extends CI_Controller{		 private $id;		 private $category_id;		 private $x_axis;		 private $percentage;		 private $frequency;		 private $avg;		 private $date;		 function __construct(){			parent::__construct();			$this->id='';			$this->category_id='';			$this->x_axis='';			$this->percentage='';			$this->frequency='';			$this->avg='';			$this->date='';		 }		 function set_id($id)		 {			$this->id=$id;		 }		 function set_category_id($category_id)		 {			$this->category_id=$category_id;		 }		 function set_x_axis($x_axis)		 {			$this->x_axis=$x_axis;		 }		 function set_percentage($percentage)		 {			$this->percentage=$percentage;		 }		 function set_frequency($frequency)		 {			$this->frequency=$frequency;		 }		 function set_avg($avg)		 {			$this->avg=$avg;		 }		 function set_date($date)		 {			$this->date=$date;		 }		 function insert_row_data(){			$this->load->model('m_graph_frequency_month');			$insert_array_data=array(					 'id' => $this->id,					 'category_id' => $this->category_id,					 'x_axis' => $this->x_axis,					 'percentage' => $this->percentage,					 'frequency' => $this->frequency,					 'avg' => $this->avg,					 'date' => $this->date					 );			return $this->m_graph_frequency_month->insert_row_data($insert_array_data);		 }	 }