<?phpclass c_information{    private $pageData;    private $CI;    function __construct(){        $this->pageData = array();        $this->CI = &get_instance();    }    function get_user_infomation_by_u_id($u_id){        include_once(APPPATH.'controllers/class_file/Profile/C_Aggrigates.php');        if (!isset($u_id) || strval($u_id) == '') {            return false;        }        $this->CI->load->model('m_user_login');        $this->CI->load->model('m_user_profile_data');        $this->CI->load->model('m_user_basic_info');        $this->CI->load->model('m_user_profile_settings');        $this->CI->load->model('m_developer_month');        $this->CI->load->model('m_developer_year');        $this->CI->load->model('m_developer_day');        $this->CI->load->model('m_developer_gender');        $this->CI->load->model('m_user_about_me');        $this->CI->load->model('m_user_trait_final_values');        $this->CI->load->model('m_gossip_main');        $this->CI->load->model('m_gossip_relationship');        $this->CI->load->model('m_gossip_insiders');        $C_Aggrigates_obj = new C_Aggrigates();        $userAggrigates = $C_Aggrigates_obj->getAggrigatesInPercentages($u_id);        $this->pageData['rankPercantage'] = $userAggrigates['rankPercantage'];        $this->pageData['reputationPercantage'] = $userAggrigates['reputationPercantage'];        $this->pageData['popularityPercantage'] = $userAggrigates['popularityPercantage'];        $this->pageData['reputationCssCls'] = $userAggrigates['reputationCssCls'];        $this->pageData['trait_final_values'] = $this->CI->m_user_trait_final_values->get_value_by_userid($u_id);        $this->pageData['insider_integrity'] = $this->CI->m_gossip_insiders->get_integrity($u_id);        $this->pageData['profile_auth_add'] = $this->CI->m_gossip_relationship->get_profile_auth($u_id);        $this->pageData['user_all_gender'] = $this->CI->m_developer_gender->get_all();        $this->pageData['user_profile_settings_all'] = $this->CI->m_user_profile_settings->get_all_by_u_id($u_id);        $this->pageData['user_login_all'] = $this->CI->m_user_login->get_all_by_uid($u_id);        $this->pageData['basic_info_all'] = $this->CI->m_user_basic_info->get_all_by_u_id($u_id);        $year = 0;        $month = 0;        $day = 0;        foreach ($this->pageData['basic_info_all'] as $rowData) {            $rowData = explode("-", $rowData->birthday);            if (is_array($rowData)) {                $year = $rowData[0];                $month = $rowData[1];                $day = $rowData[2];            }        }        $this->pageData['user_profile_data_all'] = $this->CI->m_user_profile_data->get_all_by_u_id($u_id);        $this->pageData['date_name_all'] = $this->CI->m_developer_month->get_all_name();        $this->pageData['month_data_all'] = $this->CI->m_developer_month->get_month_name_by_id($month);        $this->pageData['year_name_all'] = $this->CI->m_developer_year->get_all_year();        $this->pageData['year_name'] = $this->CI->m_developer_year->get_year_by_id($year);        $this->pageData['day_name_all'] = $this->CI->m_developer_day->get_day_all();        $this->pageData['day_name'] = $this->CI->m_developer_day->get_day_by_id($day);        $this->pageData['user_about_all'] = $this->CI->m_user_about_me->get_all_by_u_id($u_id);        return $this->pageData;    }    function get_age($u_id)    {        $this->CI->load->model('m_user_basic_info');        $birth_day = $this->CI->m_user_basic_info->get_birthday_by_u_id($u_id);        list($birth_year, $birth_month, $birth_day) = explode("-", $birth_day);        list($now_year, $now_month, $now_day) = explode("-", date('Y-m-d'));        return intval($now_year) - intval($birth_year);    }    function get_sign()    {    }    function get_profile_picture($u_id)    {        $this->CI->load->model('m_user_basic_info');        return $this->CI->m_user_basic_info->get_pic_path_by_u_id($u_id);    }    function get_inf_for_gossip_reply($u_id)    {        $this->CI->load->model('m_user_basic_info');        $this->CI->load->model('m_user_profile_data');        $this->pageData['guest_profile_pic'] = $this->CI->m_user_basic_info->get_pic_path_by_u_id($u_id);        $this->pageData['guest_name'] = $this->CI->m_user_basic_info->get_name_by_u_id($u_id);        $this->pageData['guest_location'] = $this->CI->m_user_profile_data->get_current_location_one($u_id);        return $this->pageData;    }}