<?phpclass c_search{        private $u_id;        private $name;    private $age;    private $sign;    private $gender;    private $high_school;    private $higher_education;        private $current_location_country;    private $current_location_state;    private $current_location_city;                private $home_town_country;    private $home_town_state;    private $home_town_city;        private $traits;     private $order;    function __construct() {        $this->CI = &get_instance();    }    function set_u_id($u_id){         $this->u_id=$u_id;    }   function set_name($name){            $this->name=$name;    }    function set_age($age){            $this->age=$age;    }    function set_sign($sign){            $this->sign=$sign;    }    function set_gender($gender){            $this->gender=$gender;    }    function set_high_school($high_school){            $this->high_school=$high_school;    }    function set_higher_education($higher_education){            $this->higher_education=$higher_education;    }    function set_current_location_country($current_location_country){            $this->current_location_country=$current_location_country;    }    function set_current_location_state($current_location_state){            $this->current_location_state=$current_location_state;    }    function set_current_location_city($current_location_city){            $this->current_location_city=$current_location_city;    }        function set_home_town_country($home_town_country){            $this->home_town_country=$home_town_country;    }    function set_home_town_state($home_town_state){            $this->home_town_state=$home_town_state;    }    function set_home_town_city($home_town_city){            $this->home_town_city=$home_town_city;    }        function set_traits($traits){            $this->traits=$traits;    }    function set_order($order){            $this->order=$order;    }    function get_advance_search_data(){        $this->CI->load->model("m_search");        $i=0;        if($this->name!=""){            $i++;        }        if($this->age!=""){            $i++;        }        if($this->sign!=""){            $i++;        }        if($this->gender!=""){            $i++;        }        if($this->high_school!=""){             $i++;        }        if($this->higher_education!=""){            $i++;        }               if( $this->current_location_country!=""){$i++;}        if( $this->current_location_state!=""){$i++;}        if( $this->current_location_city!=""){$i++;}        if( $this->home_town_country!=""){$i++;}        if( $this->home_town_state!=""){$i++;}        if( $this->home_town_city!=""){$i++;}        if($this->traits!=""){            $i++;        }         if($this->order!=""){            $i++;        }               if($i>0){            return $this->CI->m_search->get_search_data(                            $this->u_id,                            $this->name,                            $this->age,                            $this->sign,                            $this->gender,                            $this->high_school,                            $this->higher_education,                            $this->current_location_country,                            $this->current_location_state,                            $this->current_location_city,                            $this->home_town_country,                            $this->home_town_state,                            $this->home_town_city,                            $this->traits,                            $this->order,                            $this->traits,                            $this->order                    );        }else{            return NULL;        }    }}