<?php	 class c_user_trait_final_values extends CI_Controller{		 private $id;		 private $user_id;		 private $rank;		 private $reputation;		 private $popularity;		 private $integrity;		 private $confidence;		 private $created_date;		 function __construct(){			parent::__construct();			$this->id='';			$this->user_id='';			$this->rank='';			$this->reputation='';			$this->popularity='';			$this->integrity='';			$this->confidence='';			$this->created_date=date("Y-m-d");		 }		 function set_id($id)		 {			$this->id=$id;		 }		 function set_user_id($user_id)		 {			$this->user_id=$user_id;		 }		 function set_rank($rank)		 {			$this->rank=$rank;		 }		 function set_reputation($reputation)		 {			$this->reputation=$reputation;		 }		 function set_popularity($popularity)		 {			$this->popularity=$popularity;		 }		 function set_integrity($integrity)		 {			$this->integrity=$integrity;		 }		 function set_confidence($confidence)		 {			$this->confidence=$confidence;		 }		 function set_created_date($created_date)		 {			$this->created_date=$created_date;		 }		 function insert_row_data(){			$this->load->model('m_user_trait_final_values');			$insert_array_data=array(					 'id' => $this->id,					 'user_id' => $this->user_id,					 'rank' => $this->rank,					 'reputation' => $this->reputation,					 'popularity' => $this->popularity,					 'integrity' => $this->integrity,					 'confidence' => $this->confidence,					 'created_date' => $this->created_date					 );			return $this->m_user_trait_final_values->insert_row_data($insert_array_data);		 }                 function update_any_row(){                    $update_row_data=array();                    if($this->rank!=""){                        $update_row_data['rank']=$this->rank;                    }                    if($this->reputation!=""){                        $update_row_data['reputation']=$this->reputation;                    }                    if($this->popularity!=""){                        $update_row_data['popularity']=$this->popularity;                    }                    if($this->integrity!=""){                        $update_row_data['integrity']=$this->integrity;                    }                    if($this->confidence!=""){                        $update_row_data['confidence']=$this->confidence;                    }                    return $this->m_user_trait_final_values->update_row_data($this->user_id,$update_row_data);                 }                 function get_confidence(){                    return $this->m_user_trait_final_values->get_confidence_by_user_id($this->user_id);                 }	 }