<?php	 class c_user_visitor extends CI_Controller{		 private $id;		 private $user_id;		 private $count;		 private $created_date;		 function __construct(){			parent::__construct();			$this->id='';			$this->user_id='';			$this->count='';			$this->created_date='';		 }		 function set_id($id)		 {			$this->id=$id;		 }		 function set_user_id($user_id)		 {			$this->user_id=$user_id;		 }		 function set_count($count)		 {			$this->count=$count;		 }		 function set_created_date($created_date)		 {			$this->created_date=$created_date;		 }		 function insert_row_data(){			$this->load->model('m_user_visitor');			$insert_array_data=array(					 'id' => $this->id,					 'user_id' => $this->user_id,					 'count' => $this->count,					 'created_date' => $this->created_date					 );			return $this->m_user_visitor->insert_row_data($insert_array_data);		 }	 }