<?phpinclude_once dirname(__FILE__).'/C_BASE.php';class custom_mail extends C_BASE{	function __construct(){		parent::__construct();		$this->CI->load->helper('url');	}	function send_user_account_activation($to_email,$recipient_name,$activation_key){                $this->CI->load->model('m_mail_template');                $message="";                $subject="";                foreach($this->CI->m_mail_template->get_subject_and_message_By_type('sign_up') as $rowData){                    $message=$rowData->message;                    $subject=$rowData->subject;                }                               $message=  str_replace("[_recipient_name_]",$recipient_name, $message);                $message= str_replace("[_recipient_email_]",$to_email, $message);                                   $activation_html="<br>To Activate <a href='".base_url()."activation?".								"u_email=".$to_email.								"&activation_key=".$activation_key."' >Click Here</a><br>".								"To Decline Click Here <a href='".base_url()."decline?".								"u_email=".$to_email.								"&activation_key=".$activation_key."' >Click Here</a>";               $message=str_replace("[_activation_code_]", $activation_html, $message);                $from="Frank";                $reply_email="developer_beta@workspaceit.com";                $headers = 'MIME-Version: 1.0' . "\r\n";                $headers .= 'Content-type: text/html; charset=iso-8859-1' . "\r\n";                $headers .= "From: " .$from. "\r\n" .                            "Reply-To:".$reply_email."\r\n" .                            "X-Mailer: PHP/" . phpversion();                mail($to_email,$subject,$message,$headers);	}                function send_invitation_mail($sender_first_name,$friend_name,$friend_email){                            $to_email=$friend_email;                $from="Frank";                $subject="Invitation";                $message=" Hello ".$friend_name.",                                        <br>Your Friend ".$sender_first_name." wants to invite you to register on our cashback site.                                        <br>pelase <a href='http://www.workspaceit.com/frank'>click here <a/>to accept his invitation.                                        <br>Best Regards,".$sender_first_name." ";                $headers = 'MIME-Version: 1.0' . "\r\n";                $headers .= 'Content-type: text/html; charset=iso-8859-1' . "\r\n";                $headers .= "From: " .$from. "\r\n" .                            "Reply-To:".'infomation@frank.com'."\r\n" .                            "X-Mailer: PHP/" . phpversion();                mail($to_email,$subject,$message,$headers);        }                 function send_gossip_invitation_mail($sender_first_name,$friend_name,$friend_email,$host_server){                            $to_email=$friend_email;                $from="Frank";                $subject="Invitation";                $message=" Hello ".$friend_name.",                                        <br>Your Friend ".$sender_first_name." wants to invite you to register on our cashback site.                                        <br>pelase <a href='".$host_server."'>click here <a/>to accept his invitation.                                        <br>Best Regards,".$sender_first_name." ";                $headers = 'MIME-Version: 1.0' . "\r\n";                $headers .= 'Content-type: text/html; charset=iso-8859-1' . "\r\n";                $headers .= "From: " .$from. "\r\n" .                            "Reply-To:".'infomation@frank.com'."\r\n" .                            "X-Mailer: PHP/" . phpversion();                mail($to_email,$subject,$message,$headers);        }                }