<?php	 class gossip_relationship extends CI_Controller{		 private $u_id;         private $gender;         private $pageData;         private $site_url;         private $sign_in;         private $sign_up;         private $sign_out;         private $search;         private $home;         function __construct(){            parent::__construct();            $this->load->helper('url');            $this->pageData=array();            include_once(APPPATH.'controllers/common_site_setting.php');            include_once(APPPATH.'controllers/load_common_properties_of_user_for_ajax_request_json.php');            $this->sign_in=FALSE;            $this->sign_up=FALSE;            $this->sign_out=TRUE;            $this->search=TRUE;            $this->home=FALSE;         }		 function index(){            include_once(APPPATH.'controllers/class_file/c_gossip_relationship.php');			$c_gossip_relationship_obj=new c_gossip_relationship();			$gossip_id='';			$values='';			$point='';			$created_by='';			$c_gossip_relationship_obj->set_gossip_id($gossip_id);			$c_gossip_relationship_obj->set_values($values);			$c_gossip_relationship_obj->set_point($point);			$c_gossip_relationship_obj->set_created_by($created_by);			if($c_gossip_relationship_obj->insert_row_data()){				echo ';True;';			}else{				echo ';False;';			}		 }         function dispute(){             include_once(APPPATH.'controllers/class_file/encrypt_tbl_primary_key.php');             include_once(APPPATH.'controllers/class_file/Profile/C_Profile.php');             $encrypt_tbl_primary_key_obj=new encrypt_tbl_primary_key();             if(isset($_REQUEST['id'])){                 $rel_id=$encrypt_tbl_primary_key_obj->get_plain_text($_REQUEST['id']);             }else{                $respond_array['status']="false";                $respond_array['msg']="Clean browser cache and Refreash Your page";                json_encode($respond_array);                echo json_encode($respond_array);                die();             }             if(isset($_REQUEST['val'])){                 $val=$_REQUEST['val'];             }else{                $respond_array['status']="false";                $respond_array['msg']="Clean browser cache and Refreash Your page";                json_encode($respond_array);                echo json_encode($respond_array);                die();             }             $this->load->model('m_gossip_relationship');             $this->load->model('m_user_trait_final_values');             $updateData=array("accept"=>$val);             if($this->m_gossip_relationship->update_row_data($updateData,$rel_id)){                $respond_array['status']="true";                $created_by= $this->m_gossip_relationship->get_created_by_by_gossip_id($rel_id);                $c_Profile = new  C_Profile($this->u_id);                $c_Profile->updateProfileAuthintication($val);                if(intval($val)==-1)                    $respond_array['msg']="Disputed";                elseif(intval($val)==1)                    $respond_array['msg']="Accepted";                echo json_encode($respond_array);                die();             }else{                $respond_array['status']="false";                $respond_array['msg']="Server Error";                json_encode($respond_array);                echo json_encode($respond_array);                die();             }         }	 }?>