<?phpclass local_search extends CI_Controller{    function __construct() {                parent::__construct();        $this->load->helper('url');    }    function get_multi_user_by_namesearch(){		$this->load->model('m_user_basic_info');		$name = trim($this->input->post('user_name'));					$userInfo = $this->m_user_basic_info->get_normal_userinfo_by_search_name($name);				$replyData = array();		if(!empty($userInfo) && is_array($userInfo)){			$replyData['userInfo'] = $this->load->view('welcome/welcome_search_multi_user_info', array('userInfo'=>$userInfo),true);			$replyData['reply'] = 1;		}else {			$replyData['reply'] = 0;		}					echo json_encode($replyData);	}}