<?phpclass m_group_members extends CI_Model{	function insert_row_data($insert_data_array){		return $this->db->insert('group_members',$insert_data_array);	}    function get_favorite_by_user_id($user_id){        $sql = "SELECT * FROM user_favorite	WHERE user_id = {$user_id}";        $data = $this->db->query($sql);        if($data->num_rows() >0 ){            return $data->result_array();        }else {            return false;        }    }			function get_userlist_by_group_id($group_id){		$sql = "SELECT *				FROM group_members				WHERE user_group_id = {$group_id}";		$data = $this->db->query($sql);		if($data->num_rows() >0 ){			return $data->result_array();		}else {			return false;		}	}    function clear_favorite_member($user_id) {        $this->db->where('user_id', $user_id);        $this->db->delete('user_favorite');        return 1;    }    function remove_group_member($group_id, $member_id) {        $this->db->where('user_group_id', $group_id);        $this->db->where('members_id', $member_id);        $this->db->delete('group_members');    }    function remove_favorite_member($user_id, $favorited_id) {		$this->db->where('user_id', $user_id);		$this->db->where('favorited_id', $favorited_id);		$this->db->delete('user_favorite');	}}