 	$('#advance_search_data').on('click',function(e){        	e.preventDefault();    		get_advance_search_data();    		$('#selected_user_content_div').find('.as_sr_container').remove();        });        		/**		 * select / unselect search user list 		 */    	$('#search_result_content_div').on('click','div.user_select',function(e){        	var $this = $(this),                parentDivClass="."+$this.parent().attr("class");        	parentDiv = $this.parent(),        	selectedContent = parentDiv.clone().hide(),        	divID = parentDiv.attr('id');        	selectedContent.find('div.user_select').remove();        	var $totalUserDiv = $('#selected_user_content_div').find('span#total_selected_list'),			totalSelected = parseInt($totalUserDiv.text().trim());        	        	if($this.hasClass('as_circle_plus')){        		$this.removeClass('as_circle_plus');        		$this.addClass('as_circle_plus_act');				$('#selected_list').append(selectedContent).find(parentDivClass).last().fadeIn(500);				$totalUserDiv.text(++totalSelected);        		            }else {            	$this.removeClass('as_circle_plus_act');                	$this.addClass('as_circle_plus');                    $('#selected_list').find('#'+divID).fadeOut(500,function(e){                     $(this).remove();                    $totalUserDiv.text(--totalSelected);                });            	            }        });		        $('button#comoare_result').on('click',function(e){            e.preventDefault();            var searchUserList = $('form#form_search_user').serializeArray();        	if(searchUserList.length > 0 ){            	userInputID = $('#search_result_content_div').find('input.hidden_user_id');            	$("#user_hidden_id").html(userInputID);        		$('form#search_content_form').submit();            } else {                alert('No result found to compare');            }        });        $('button#compare_selected').on('click',function(e){            e.preventDefault();            var selectedUserList = $('form#form_selected_user').serializeArray();        	if(selectedUserList.length > 0 ){            	userInputID = $('#selected_list').find('input.hidden_user_id');            	$("#user_hidden_id").html(userInputID);        		$('form#search_content_form').submit();            } else {                alert('No result found to compare');            }        });    		});	ddlevelsmenu.setup("ddtopmenubar_for", "topbar"); //ddlevelsmenu.setup("mainmenuid", "topbar|sidebar")	function change_select_view_for_search(elem){		$("#sub_trait").val($(elem).attr("t_id"));		$('#selectedTraitView' ).html($(elem).html());	}	function selectTraitBox(id,trait){		$("#"+id).find("a.selectTrait").each(function(){			if($(this).html().trim().toUpperCase() == trait.toUpperCase()){				$(this).trigger("click");				getTraitUserList();				getGraphDayDataList();				getHighestTraitUserByTraitId();				return;			}		});	}	$(document).ready(function(){		selectTraitBox("ddsubmenu1","rank");	});</script><?php include 'footer.php' ?>