ml("").html(replyData).fadeIn();                        });                    }                });            }        });        $('a#clear_favorites').on('click', function (e) {            e.preventDefault();            reply = confirm('Are you sure you want to clear Favorites?');            if(reply) {                $.ajax({                    url: BASE + 'group/clear_favorites',                    type: 'POST',                    success: function (replyData) {                        if (replyData == 1) {                            load_notification_bar("Favorites Cleared");                            show_success_then_hide(3000, "");                            location.reload();                        } else {                            load_notification_bar("Clear Favorite Failed");                            show_error_then_hide(3000, "");                        }                    }                });            }        });		$('button.quote_cancel').on('click',function(){			$this = $(this);			$container = $this.parents('.bubble_container');			$container.fadeOut('slow',function(){				$container.find('input[type=text]').val("");			});		});		$('#create_group_popup').on('click','.quote_save',function(){			$this = $(this);			$container = $this.parents('.bubble_container');			group_name = $container.find('input[name=group_name]').val();			group_id = $('#group_id').val();			if(group_name.length > 0){				$.ajax({					url : BASE+'group/update_group_name',					type: 'POST',					data : { group_name : group_name, group_id : group_id },					success : function(replyData) {						if(replyData==1){							load_notification_bar("Group name update");							delay_and_hide_notification_bar(3000);							$container.fadeOut('slow',function(){								$container.find('input[type=text]').val("");							});							load_notification_bar("Group created");							delay_and_hide_notification_bar(3000);							window.location.href = BASE+'group/my_group?gname='+group_name;						}else {							load_notification_bar("Group name already exists");							delay_and_hide_notification_bar(3000);						}					}				});			}else {				alert("Please insert group name");			}		});		$('button.remove_group_member').on('click',function(){			$this = $(this);			var parentContainer = $this.parents('.pe_container');			var member_id = parentContainer.find('input[name=user_id]').val(),			reply = confirm('Are you sure to delete member?');			if(reply){				$.ajax({					url : BASE+'group/remove_favorite_member',					type: 'POST',					data : { member_id : member_id },					success : function(r) {						if(r) {							location.reload();						}					}				});			}		});		getGraphDayDataList = function (){			//var graphData;			var formData = $('form#group_form').serializeArray();			$.ajax({				url : BASE+'search/get_graphday_datalist/',				type : 'POST',				data : formData,				success : function(reply) {					var replyData = $.parseJSON(reply);					if(replyData.reply==1){						graphData = replyData.graphData;						//console.log(graphData[0].percentage);						drawDayCanvas(graphData[0].percentage);					}else {					}				}			});		};		getGraphDayDataList();		getGraphYearDataList = function (){			//var graphData;			var formData = $('form#group_form').serializeArray();			$.ajax({				url : BASE+'search/get_graphyear_datalist/',				type : 'POST',				data : formData,				success : function(reply) {					var replyData = $.parseJSON(reply);					if(replyData.reply==1){						graphData = replyData.graphData;						drawYearCanvas(graphData[0].frequency);					}else {					}				}			});		};		getGraphYearDataList();		function drawDayCanvas(graphFrequencyDay){			graphFrequencyDayData = graphFrequencyDay.split(",");			var c = document.getElementById("g1");			if(c){				var ctx=c.getContext("2d");				ctx.beginPath();				ctx.moveTo(0,190);				var y;				//console.log(graphFrequencyDayData.length);				for(var i=0;i<graphFrequencyDayData.length;i++){					y = Math.floor((parseInt(graphFrequencyDayData[i])*190)/100);					//console.log(parseInt(graphFrequencyDayData[i]));					y = 190-y;					ctx.lineTo(i*1.85,y);				}				//console.log(a);				ctx.lineTo(370,190);				// add linear gradient				var grd = ctx.createLinearGradient(370, 0, 370, 190);				// light blue				grd.addColorStop(0, '#8ED6FF');				// dark blue				grd.addColorStop(1, '#004CB3');				ctx.fillStyle = grd;				ctx.lineJoin = 'round';				ctx.lineWidth = 1;				//ctx.fillStyle = '#3972C1';				ctx.shadowColor = '#999';				ctx.shadowBlur = 0;				ctx.shadowOffsetX = 2;				ctx.shadowOffsetY = 2;				ctx.fill();				var x = 18.5;				for(var j = 0 ; j<20; j++){					ctx.beginPath();					ctx.moveTo(x,0);					ctx.lineTo(x,190);					ctx.shadowBlur = 0;					ctx.shadowColor= '#fff';					ctx.strokeStyle = '#000000';					ctx.shadowColor = '#333';					ctx.shadowBlur = 0;					ctx.shadowOffsetX = 0;					ctx.shadowOffsetY = 0;					if(j==8){						x +=17.5;						ctx.lineWidth = 1;						ctx.stroke();					}					else if(j==9){						x +=18.5;						ctx.lineWidth = 3;						ctx.stroke();					}					else{						x +=18.5;						ctx.lineWidth = 1;						ctx.stroke();					}				}			}		}		function drawYearCanvas(graphFrequencyData1){			graphFrequencyData = graphFrequencyData1.split(",");			var c=document.getElementById("g2");			var arr1 = new Array();			var arr2 = new Array();			if(c){				var ctx=c.getContext("2d");				//////////				ctx.beginPath();				ctx.moveTo(0,95);				var y;				for(var i=0; i< graphFrequencyData.length;){					if(graphFrequencyData[i] >= 0){						y = Math.floor((graphFrequencyData[i]*95)/100);						arr1.push(y);						y = (95-y);						ctx.lineTo(i,y);						i = i+1;					}else {						y = Math.floor(null*95);						arr1.push(y);						y = (95-y);						//ctx.lineTo(i,y);						i = i+1;					}				}				ctx.lineTo(370,95);				// add linear gradient				var grd = ctx.createLinearGradient(370, 0, 370, 95);				// light blue				grd.addColorStop(0, '#8ED6FF');				// dark blue				grd.addColorStop(1, '#004CB3');				ctx.fillStyle = grd;				ctx.lineJoin = 'round';				ctx.lineWidth = 1;				//ctx.fillStyle = '#3972C1';				ctx.shadowColor = '#999';				ctx.shadowBlur = 0;				ctx.shadowOffsetX = 2;				ctx.shadowOffsetY = 2;				ctx.fill();				/////////////////				ctx.beginPath();				ctx.moveTo(0,95);				var y;				for(var i=0;i<graphFrequencyData.length;){					if(graphFrequencyData[i] <= 0){						y = Math.floor((Math.abs(graphFrequencyData[i])*95)/100);						arr2.push(y);						y = 95+y;						ctx.lineTo(i,y);						i = i+1;					}else {						y = Math.floor(null*95);						arr2.push(y);						y = (95-y);						//ctx.lineTo(i,y);						i = i+1;					}				}				ctx.lineTo(370,95);				// add linear gradient				var grd = ctx.createLinearGradient(370, 95, 370, 190);				// light blue				grd.addColorStop(1, '#8ED6FF');				// dark blue				grd.addColorStop(0, '#004CB3');				ctx.fillStyle = grd;				ctx.lineJoin = 'round';				ctx.lineWidth = 1;				//ctx.fillStyle = '#3972C1';				ctx.shadowColor = '#999';				ctx.shadowBlur = 0;				ctx.shadowOffsetX = 2;				ctx.shadowOffsetY = 2;				ctx.fill();				ctx.beginPath();				ctx.moveTo(0,95);				//console.log(arr1.length);				arr1.reverse();				arr2.reverse();				for(var i=0; i< graphFrequencyData.length;){					var p1 = arr1.pop();					var p2 = arr2.pop();					var yy = p1 - p2;					if(yy < 0){						yy =  (95 + Math.abs(yy));					}					else{						yy =  95-yy;					}					//console.log(p1+':'+p2+':'+yy);					//ctx.lineTo(i,yy);					i = i+10;				}				ctx.lineTo(370,95);				ctx.lineWidth = 2;				ctx.strokeStyle = '#222';				ctx.shadowColor = '#222';				ctx.shadowBlur = 0;				ctx.shadowOffsetX = 0;				ctx.shadowOffsetY = 0;				ctx.stroke();				/////////////				ctx.beginPath();				ctx.moveTo(0,95);				ctx.lineTo(370,95);				ctx.lineWidth = 2;				ctx.strokeStyle = '#000000';				ctx.shadowColor = '#333';				ctx.shadowBlur = 0;				ctx.shadowOffsetX = 0;				ctx.shadowOffsetY = 0;				ctx.stroke();				///////////				var x = 30.83;				for(var j = 0 ; j<20; j++){					ctx.beginPath();					ctx.moveTo(x,0);					ctx.lineTo(x,190);					ctx.shadowBlur = 0;					ctx.shadowColor= '#fff';					ctx.strokeStyle = '#000000';					ctx.shadowColor = '#333';					ctx.shadowBlur = 0;					ctx.shadowOffsetX = 0;					ctx.shadowOffsetY = 0;					x +=30.83;					ctx.lineWidth = 1;					ctx.stroke();				}			}		}	});</script><!--</div>--><!--Footer>--><?php include dirname(__FILE__).'/../footer.php' ?><script type="text/javascript">	set_postion_fixed();	initialize_notification_bar("42%","31%");</script><div id="gosssip_reply_first_col" style="display: none;">	<div  class="goss_small_comment">		<!--                    <div class="spec_circle_purple" style="margin:15px 0 0 -10px;">                              <p class="round_span_small"></p>                              <p class="round_span_small"></p>                            </div>-->		<div class="goss_teller_small" style="margin-left: 27px;" >			<?php if($guest_gossip_reply['guest_profile_pic']!=""){ ?>				<img src="<?php echo base_url()."uploads/".$guest_gossip_reply['guest_profile_pic']; ?>" >			<?php }else{ ?>				<img src="<?php echo base_url().'images/user.png'; ?>" >			<?php } ?>			<div class="goss_teller_small_inf">				<p><?php echo $guest_gossip_reply['guest_name']; ?></p><span><?php echo $guest_gossip_reply['guest