lic; font-weight: bold; color: #000; font-size: 16px;">Didn't						find someone you were looking for?</p>				</div>				<div class="leave_gossip">					<a href="#" style="text-decoration: none; color: #2E3192;"><p							style="margin: 5px 0 0 0;">Leave gossip and invite them to join</p>				</div>				</a>			</div>		</div>	</div>	<!--</div>-->	<script type="text/javascript">		function  getTraitUserList(search){			var offset = 1;			if(search){				$('#offset').val(offset);			}else{				offset = parseInt($('#offset').val());				$('#offset').val(++offset);			}			var formData = $('form#trait_form').serializeArray();			$.ajax({				url : BASE+'welcome/get_trait_user_list/',				type : 'POST',				data : formData,				beforeSend : function() {					//console.log('loading...');				},				success : function(reply) {					var replyData = $.parseJSON(reply);					if(replyData.reply==1){						$('div.middle_left').html(replyData.viewData);					}				}			});		}			$(function(){				$('a.selectTrait').on('click',function(e){					e.preventDefault();					$this = $(this);					var traitName = $this.text(),						traitValue = $this.attr('href');					//img = $('#selectedView').find('img').html();					$('#selectedView').text(traitName);					$('input[name=traits_id]').val(traitValue);				});			    getHighestTraitUserByTraitId = function(){			    	var formData = $('form#trait_form').serializeArray();			        $.ajax({			            url : BASE+'welcome/get_highest_traituser_by_traitid/',			            type : 'POST',			            data : formData,			            beforeSend : function() {				            //console.log('loading...');			            },			            success : function(reply) {			                var replyData = $.parseJSON(reply);			                if(replyData.reply==1){			                    $('#highest_trait_user_content').fadeOut().html("");			                    $('#highest_trait_user_content').html(replyData.viewData).fadeIn();			                }			            }			        });			    };			    getHighestTraitUserByTraitId();			   // getTraitUserList();			    $('#submit_trait_btn').on('click',function(e){			        e.preventDefault();			    	getTraitUserList(true);			    	getGraphDayDataList();			    	getHighestTraitUserByTraitId();			    });			    $('a#search_person').on('click',function(e){				    e.preventDefault();				    personName = $('input[name=person_name]').val().trim();				    //console.log($personName);				    if(personName.length > 0){						$.ajax({							url : BASE+'welcome/get_user_by_namesearch',							type : 'POST',							data : { user_name : personName },							success : function(replyData){								var data = $.parseJSON(replyData);								if(data.reply==1){									$('#search_person_info').html("").html(data.userInfo);									$('#search_person_info').fadeIn('slow');								}							}						});					}				});			    getGraphDayDataList = function (){					//var graphData;			    	var formData = $('form#trait_form').serializeArray();			        $.ajax({			            url : BASE+'welcome/get_graphday_datalist/',			            type : 'POST',			            data : formData,			            success : function(reply) {			                var replyData = $.parseJSON(reply);			                if(replyData.reply==1){			                	graphData = replyData.graphData;			                	drawDayCanvas(graphData[0].percentage);			                }else {				            }			            }			        });			    };				getGraphDayDataList();			    getGraphYearDataList = function (){					//var graphData;			    	var formData = $('form#trait_form').serializeArray();			        $.ajax({			            url : BASE+'welcome/get_graphyear_datalist/',			            type : 'POST',			            data : formData,			            success : function(reply) {			                var replyData = $.parseJSON(reply);			                if(replyData.reply==1){			                	graphData = replyData.graphData;			                	drawYearCanvas(graphData[0].frequency);			                }else {				            }			            }			        });			    };			    getGraphYearDataList();				function drawDayCanvas(graphFrequencyDay){					var graphFrequencyDayData = graphFrequencyDay.split(",");					var c = document.getElementById("g1");					var height = 190;				   	if(c){						var ctx=c.getContext("2d");						ctx.beginPath();						ctx.moveTo(0,height);						var y;						var prevVal = 0;						var maxScore = getMaxScore(graphFrequencyDayData);						$("#topRange").html(Math.round(maxScore)+" %");						$("#midRange").html(Math.round(maxScore/2)+" %");						$("#lowRange").html("0 %");						for(var i=0;i<graphFrequencyDayData.length-1;i++){                                                        //console.log(graphFrequencyDayData[i]);							var graphFrequencyVal = graphFrequencyDayData[i];//							if(graphFrequencyVal ==0){//								graphFrequencyVal = prevVal - 5 ;//							}//							prevVal = graphFrequencyVal;							y = Math.floor((parseInt(graphFrequencyVal)*height)/100);							y = ( height * graphFrequencyVal ) /maxScore;							y = height-y;							if(graphFrequencyDayData[i]!=0)								console.log(graphFrequencyVal+" Max:"+maxScore+" Y: "+y +" i:"+i);							ctx.lineTo(i*1.85,y);						}						//console.log(a);						ctx.lineTo(370,height);						// add linear gradient						var grd = ctx.createLinearGradient(370, 0, 370, height);						// light blue						//grd.addColorStop(0, '#8ED6FF');						// dark blue						grd.addColorStop(1, '#004CB3');						ctx.fillStyle = grd;						//ctx.lineJoin = 'round';						ctx.lineWidth = 1;						//ctx.fillStyle = '#3972C1';//						ctx.shadowColor = '#999';//						ctx.shadowBlur = 0;//						ctx.shadowOffsetX = 2;//						ctx.shadowOffsetY = 2;						ctx.fill();					    var x = 18.5;					    for(var j = 0 ; j<20; j++){						    ctx.beginPath();						    ctx.moveTo(x,0);						    ctx.lineTo(x,height);						    ctx.shadowBlur = 0;						    ctx.shadowColor= '#fff';						    ctx.strokeStyle = '#000000';						    ctx.shadowColor = '#333';						    ctx.shadowBlur = 0;						    ctx.shadowOffsetX = 0;						    ctx.shadowOffsetY = 0;						    if(j==8){							    x +=17.5;							    ctx.lineWidth = 1;							    ctx.stroke();					    	}						    else if(j==9){							    x +=18.5;							    ctx.lineWidth = 0.3;							    ctx.stroke();						    }						    else{							    x +=18.5;							    ctx.lineWidth = 0.5;							    ctx.stroke();						    }					    }					}				}				function drawYearCanvas(graphFrequencyData1){					graphFrequencyData = graphFrequencyData1.split(",");					var c=document.getElementById("g2");					var arr1 = new Array();					var arr2 = new Array();				   	if(c){						var ctx=c.getContext("2d");						//////////						ctx.beginPath();						ctx.moveTo(0,95);						var y;						for(var i=0; i< graphFrequencyData.length;){							if(graphFrequencyData[i] >= 0){								y = Math.floor((graphFrequencyData[i]*95)/100);								arr1.push(y);								y = (95-y);								ctx.lineTo(i,y);								i = i+1;							}else {								y = Math.floor(null*95);								arr1.push(y);								y = (95-y);								//ctx.lineTo(i,y);								i = i+1;							}						}						ctx.lineTo(370,95);						// add linear gradient				    	var grd = ctx.createLinearGradient(370, 0, 370, 95);				      	// light blue				      	grd.addColorStop(0, '#8ED6FF');				      	// dark blue				      	grd.addColorStop(1, '#004CB3');				      	ctx.fillStyle = grd;						ctx.lineJoin = 'round';						ctx.lineWidth = 1;					    //ctx.fillStyle = '#3972C1';					    ctx.shadowColor = '#999';					    ctx.shadowBlur = 0;					    ctx.shadowOffsetX = 2;					    ctx.shadowOffsetY = 2;					    ctx.fill();					    /////////////////					    ctx.beginPath();						ctx.moveTo(0,95);						var y;						for(var i=0;i<graphFrequencyData.length;){							if(graphFrequencyData[i] <= 0){								y = Math.floor((Math.abs(graphFrequencyData[i])*95)/100);								arr2.push(y);								y = 95+y;								ctx.lineTo(i,y);								i = i+1;							}else {								y = Math.floor(null*95);								arr2.push(y);								y = (95-y);								//ctx.lineTo(i,y);								i = i+1;							}						}						ctx.lineTo(370,95);						// add linear gradient				      	var grd = ctx.createLinearGradient(370, 95, 370, 190);				      	// light blue				      	grd.addColorStop(1, '#8ED6FF');				      	// dark blue				      	grd.addColorStop(0, '#004CB3');				      	ctx.fillStyle = grd;						ctx.lineJoin = 'round';						ctx.lineWidth = 1;					    //ctx.fillStyle = '#3972C1';					    ctx.shadowColor = '#999';					    ctx.shadowBlur = 0;					    ctx.shadowOffsetX = 2;					    ctx.shadowOffsetY = 2;					    ctx.fill();					    ctx.beginPath();						ctx.moveTo(0,95);						//console.log(arr1.length);						arr1.reverse();						arr2.reverse();					    for(var i=0; i< graphFrequencyData.length;){						    var p1 = arr1.pop();						    var p2 = arr2.pop();						    var yy = p1 - p2;						    if(yy < 0){						    	yy =  (95 + Math.abs(yy));						    }						    else{						    	yy =  95-yy;						    }						   //console.log(p1+':'+p2+':'+yy);						    //ctx.lineTo(i,yy);						    i = i+10;					    }					    ctx.lineTo(370,95);						ctx.lineWidth = 2;						ctx.strokeStyle = '#222';						ctx.shadowColor = '#222';					    ctx.shadowBlur = 0;					    ctx.shadowOffsetX = 0;					    ctx.shadowOffsetY = 0;					    ctx.stroke();						/////////////						ctx.beginPath();						ctx.moveTo(0,95);						ctx.lineTo(370,95);						ctx.lineWidth = 2;						ctx.strokeStyle = '#000000';						ctx.shadowColor = '#333';						ctx.shadowBlur = 0;					    ctx.shadowOffsetX = 0;					    ctx.shadowOffsetY = 0;					    ctx.stroke();					    ///////////					    var x = 30.83;					    for(var j = 0 ; j<20; j++){						    ctx.beginPath();						    ctx.moveTo(x,0);						    ctx.lineTo(x,190);						    ctx.shadowBlur = 0;						    ctx.shadowColor= '#fff';						    ctx.strokeStyle = '#000000';						    ctx.shadowColor = '#333';						    ctx.shadowBlur = 0;						    ctx.shadowOffsetX = 0;						    ctx.shadowOffsetY = 0;						    x +=30.83;						    ctx.lineWidth = 1;						    ctx.stroke();					    }					}			    }			});			function getMaxScore(arr){				var max = Math.max.apply(null, arr);				return max;			}			function selectTraitBox(id,trait){				$("#"+id).find("a.selectTrait").each(function(){					if($(this).html().trim().toUpperCase() == trait.toUpperCase()){						$(this).trigger("click");						getTraitUserList(true);						getGraphDayDataList();						getHighestTraitUserByTraitId();						return;					}				});			}			$(document).ready(function(){				selectTraitBox("ddsubmenu1","rank");			});		</script>	<!--Footer>-->	<?php include 'footer.php' ?>	<input type='hidden' id='after_login_redirect_url' value='<?php echo $after_login_redirect_url; ?>' /><script>    set_postion_fixed();    initialize_notification_bar("42%","31%");</script>