vance_search_data').on('click', function(e) {            e.preventDefault();            get_advance_search_data();            $('#selected_user_content_div').find('.as_sr_container').remove();        });        /**                  * select / unselect search user list                   */        $('#search_result_content_div').on('click', 'div.user_select', function(e) {            var $this = $(this),                    parentDivClass = "." + $this.parent().attr("class");            parentDiv = $this.parent(),                    selectedContent = parentDiv.clone().hide(),                    divID = parentDiv.attr('id');            selectedContent.find('div.user_select').remove();            var $totalUserDiv = $('#selected_user_content_div').find('span#total_selected_list'),                    totalSelected = parseInt($totalUserDiv.text().trim());            if ($this.hasClass('as_circle_plus')) {                $this.removeClass('as_circle_plus');                $this.addClass('as_circle_plus_act');                $('#selected_list').append(selectedContent).find(parentDivClass).last().fadeIn(500);                $totalUserDiv.text(++totalSelected);            } else {                $this.removeClass('as_circle_plus_act');                $this.addClass('as_circle_plus');                $('#selected_list').find('#' + divID).fadeOut(500, function(e) {                    $(this).remove();                    $totalUserDiv.text(--totalSelected);                });            }        });        $('button#comoare_result').on('click', function(e) {            e.preventDefault();            var searchUserList = $('form#form_search_user').serializeArray();            if (searchUserList.length > 0) {                userInputID = $('#search_result_content_div').find('input.hidden_user_id');                $("#user_hidden_id").html(userInputID);                $('form#search_content_form').submit();            } else {                alert('No result found to compare');            }        });        $('button#compare_selected').on('click', function(e) {            e.preventDefault();            var selectedUserList = $('form#form_selected_user').serializeArray();            if (selectedUserList.length > 0) {                userInputID = $('#selected_list').find('input.hidden_user_id');                $("#user_hidden_id").html(userInputID);                $('form#search_content_form').submit();            } else {                alert('No result found to compare');            }        });    });</script><script>    function load_invite_friend_row() {        var size = $('#invite_friend_row_size').val();        $('#invite_friend_row_size').attr("value", ++size);        $.ajax({            type: "post",            data: {size: size},            url: $('#base_url').val() + "user_invitation/load_invitation_row_for_signup",            success: function (data) {                $('#invite_friends_content_div').append(data);                $('.invite_friend_row').fadeIn(500);            }        });    }    $(document).ready(function () {        $('.tabs > li > a').click(function (event) {            event.preventDefault();            var active_tab_selector = $('.tabs > li.active > a').attr('href');            var actived_nav = $('.tabs > li.active');            var target_tab_selector = $(this).attr('href');            if(target_tab_selector == "#tab1" || target_tab_selector == "#tab2"){                return;            }            actived_nav.removeClass('active');            $(this).parents('li').addClass('active');            $(active_tab_selector).removeClass('active');            $(active_tab_selector).addClass('hide');            $(target_tab_selector).removeClass('hide');            $(target_tab_selector).addClass('active');        });        $("#preference_li .tab_a").trigger("click");        var prefferenceData = JSON.parse($("#preferenceData").val());         $('#birthday_hidden').val(prefferenceData[0].birthday_hidden);         $('#home_page').val(prefferenceData[0].home_page);         $('#security').val(prefferenceData[0].security);         $('#gossip').val(prefferenceData[0].gossip);         $('#notification').val(prefferenceData[0].notification);    });    function update_preference_setting() {        load_notification_bar("Processing Data...");       // disable_all_element('preference_settings_div', 'input');        $.ajax({            url: $("#base_url").val()+'my_profile/update_preference_string',            data: {                birthday_hidden: $('#birthday_hidden').val(),                home_page: $('#home_page').val(),                security: $('#security').val(),                gossip: $('#gossip').val(),                notification: $('#notification').val()            },            type: "POST",            success: function (data) {                var resp = data.split(";");                if (resp[1] == "True") {                    hide_notification_bar();                } else if (resp[1] == "False") {                }            }, error: function (jqXHR, textStatus, errorThrown) {                hide_notification_bar();            }        });    }    function submit_invite_friend() {        var sender_first_name = $('#f_name').val();        var i = 0;        load_notification_bar("Processing Data....");        disable_all_element('invite_friends_content_div', 'input');        set_effect_on_content("tab4");        $("input[id^='friend_name']").each(function () {            var friend_name = $(this).val();            var friend_email = $(this).next().val();            if (friend_name != "" && friend_email != "") {                i++;                $.ajax({                    type: "post",                    url: $('#base_url').val() + "my_profile/update_invite_friends_data",                    data: {                        friend_name: friend_name,                        friend_email: friend_email,                        sender_first_name: sender_first_name                    },                    success: function (data) {                        enable_all_element('invite_friends_content_div', 'input');                        set_defult_effect_on_content("tab4");                        hide_notification_bar();                    }                });            }        });        if (i == 0) {            enable_all_element('invite_friends_content_div', 'input');            hide_notification_bar();            set_defult_effect_on_content("tab4");        }    }</script><?php include dirname(__FILE__).'/../footer.php' ?>